import axios from 'axios';
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

// App
class App extends React.Component {
  state = { details: [], selectedDate: new Date() };

  componentDidMount() {
    axios.get('http://127.0.0.1:8000/')
      .then(res => {
        const data = res.data;
        this.setState({ details: data });
      })
      .catch(err => {
        console.error("Error fetching data:", err);
      });
  }

  render() {
    return (
      <Router>
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/calendar">Calendar</Link></li>
            <li><Link to="/about">About</Link></li>
          </ul>
        </nav>

        <Routes>
          <Route path="/" element={<Home details={this.state.details} />} />
          <Route
            path="/calendar"
            element={
              <CalendarTile
                entries={this.state.details}
                selectedDate={this.state.selectedDate}
                onDateChange={(date) => this.setState({ selectedDate: date })}
              />
            }
          />
          <Route path="/about" element={<About />} />
        </Routes>
      </Router>
    );
  }
}

// Home Component
const Home = ({ details }) => (
  <div>
    <h1>Home</h1>
    <ul>
      {details.map((item, id) => (
        <li key={id}>{item.date} - {item.journal_entry}</li>
      ))}
    </ul>
  </div>
);

// CalendarTile Component
const CalendarTile = ({ entries, selectedDate, onDateChange }) => {
  if (!Array.isArray(entries)) return null; // Add guard clause in case entries is undefined

  const tileContent = ({ date }) => {
    const entry = entries.find((entry) => entry.date === date.toISOString().split('T')[0]);
    return entry ? (
      <div
        style={{
          backgroundColor: entry.mood_color,
          height: '100%',
          width: '100%',
          borderRadius: '50%',
        }}
      />
    ) : null;
  };

  return (
    <div>
      <h1>Calendar</h1>
      <Calendar
        onChange={onDateChange}
        value={selectedDate}
        tileContent={tileContent}
      />
    </div>
  );
};

// About Component
const About = () => (
  <div>
    <h1>About This App</h1>
    <p>This is a therapy journaling app to track your mood and entries!</p>
  </div>
);

export default App;
