import { React } from 'react';
import { Box, TextField, Typography, Button } from '@mui/material';
import BasicDatePicker from '../components/EntryForm/EntryDatePicker';
import MultilineTextFields from '../components/EntryForm/EntryMultilineTextField';
import BasicTextFields from '../components/EntryForm/EntryTextField';
import SelectVariants from '../components/EntryForm/EntrySelectField';
import { useForm } from 'react-hook-form';
import Checkboxes from '../components/EntryForm/EntryCheckBox';
import AxiosInstance from '../components/axios';
import dayjs from 'dayjs';

const Entry = () => {
  const defaultValues = {
    entry_date: dayjs(), // Set the default to current date using Day.js
    entry_content: '',
    mood_color: '',
    proper_nutrition: '',
    proper_hydration: '',
    hydration_amount: '',
    proper_exercise: '',
    exercise_duration: '',
    exercise_description: '',
    hours_of_sleep: ''
  };

  const { handleSubmit, reset, setValue, control, watch } = useForm({ defaultValues: defaultValues });

  const submission = (data) => {
    const formatted_date = data.entry_date.format("YYYY-MM-DD"); // Format Day.js object to string
    AxiosInstance.post('/', {
      entry_date: formatted_date,
      entry_content: data.entry_content,
      mood_color: data.mood_color,
      proper_nutrition: data.proper_nutrition,
      proper_hydration: data.proper_hydration,
      hydration_amount: data.hydration_amount,
      proper_exercise: data.proper_exercise,
      exercise_duration: data.exercise_duration,
      exercise_description: data.exercise_description,
      hours_of_sleep: data.hours_of_sleep
    });
  };

  return (
    <div style={{ width: '100%', overflowX: 'hidden' }}> {/* Prevent horizontal scrolling */}
      <form onSubmit={handleSubmit(submission)}>
        <Box
          sx={{
            display: 'flex',
            width: '100%',
            marginBottom: '10px',
            marginTop: '10px',
            padding: '2px',
            alignItems: 'center',
          }}
        >
          <Typography
            sx={{
              marginLeft: '20px',
              color: '#000000',
              fontWeight: 'bold',
              fontSize: '1.5rem',
            }}
          >
            Start a New Journal Entry
          </Typography>
        </Box>

        <Box
          sx={{
            display: 'flex',
            width: '100%',
            boxShadow: 3,
            padding: 4,
            flexDirection: 'column',
            boxSizing: 'border-box',
          }}
        >
          <Box sx={{ display: 'flex', flexDirection: 'column', width: '100%' }}>
            <BasicDatePicker label="Entry Date" name="entry_date" control={control} /> {/* Handle entry_date as a Day.js object */}

            <MultilineTextFields
              label="Name"
              name="entry_content"
              control={control}
              placeholder="Provide an entry name"
            />

            <SelectVariants name="mood_color" control={control} />

            <Checkboxes name="proper_nutrition" control={control} label="Proper Nutrition" />
            <Checkboxes name="proper_hydration" control={control} label="Proper Hydration" />
            <Checkboxes name="proper_exercise" control={control} label="Proper Exercise" />

            <BasicTextFields name="exercise_duration" control={control} label="Exercise duration" />
            <BasicTextFields name="exercise_description" control={control} label="Exercise description" />
            <BasicTextFields name="hours_of_sleep" control={control} label="Hours of sleep" />
          </Box>

          <Box>
            <Button variant="contained" type="submit" sx={{ width: '100%' }}>
              Submit
            </Button>
          </Box>
        </Box>
      </form>
    </div>
  );
};

export default Entry;
