import axios from 'axios';
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// Calendar Tile Component
const CalendarTile = ({ date, entries }) => {
  const entry = entries.find((entry) => entry.date === date.toISOString().split('T')[0]);

  return entry ? (
    <div
      style={{
        backgroundColor: entry.mood_color,
        height: '100%',
        width: '100%',
        borderRadius: '50%',
      }}
    />
  ) : null;
};

// app
class App extends React.Component {
  state = { details: [] };

  componentDidMount() {
    axios.get('http://127.0.0.1:8000/')
      .then(res => {
        const data = res.data;
        this.setState({ details: data });
      })
      .catch(err => {
        console.error("Error fetching data:", err);
      });
  }

  render() {
    return (
      <Router>
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/calendar">Calendar</Link></li>
            <li><Link to="/about">About</Link></li>
          </ul>
        </nav>
        <Routes>
          <Route path="/" element={<Home details={this.state.details} />} />
          <Route path="/calendar" element={<CalendarPage details={this.state.details} />} />
          <Route path="/about" element={<About />} />
        </Routes>
      </Router>
    );
  }
}

// Separate components for each page
const Home = ({ details }) => (
  <div>
    <h1>Home</h1>
    <ul>
      {details.map((item, id) => (
        <li key={id}>{item.date} - {item.journal_entry}</li>
      ))}
    </ul>
  </div>
);

const CalendarPage = ({ details }) => (
  <div>
    <h1>Calendar</h1>
    {/* Place your Calendar component here */}

  </div>
);

const About = () => (
  <div>
    <h1>About This App</h1>
    <p>This is a therapy journaling app to track your mood and entries!</p>
  </div>
);

export default App;
