import axios from 'axios'; // Correct import
import React from "react";

import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

import './App.css';

class App extends React.Component {
  state = { details: [] }; // Properly initialized state

  componentDidMount() {
    axios.get('http://127.0.0.1:8000/') // Fetch data from Django backend
      .then(res => {
        const data = res.data; // Store response data
        this.setState({
          details: data, // Update state with fetched data
        });
      })
      .catch(err => {
        console.error("Error fetching data:", err); // Handle errors
      });
  }

  render() {
    return (


      <div>
        <h1>MyTherapy</h1> {/* Fixed Header */}
        <hr />

        {/* Entries will populate below */}
        {this.state.details.map((output, id) => ( // Fixed .map() syntax
          <div key={id}> {/* Added unique key for each item */}
            <h4>{output.date} | {output.mood_color} | {output.journal_entry}</h4>

          </div>
        ))}


        {/* Add Calendar Below */}
        <div className="calendar-container">
      <Calendar
        onChange={(date) => this.setState({ selectedDate: date })}
        tileContent={({ date, view }) => {
          const entry = this.state.details.find(
            (detail) => detail.date === date.toISOString().split('T')[0]
          );
          return entry ? (
            <div
              style={{
                backgroundColor: entry.mood_color,
                height: '100%',
                width: '100%',
                borderRadius: '50%',
              }}
            />
          ) : null;
        }}
      />
      </div>
      </div>
    );
  }
}

export default App;
