import React, { useState, useEffect } from 'react';

const JournalForm = () => {
  const [formData, setFormData] = useState({
    date: '',
    content: '',
    mood_color: '',
    proper_nutrition: false,
    proper_hydration: false,
    hydration_amount: '',
    proper_exercise: false,
    exercise_duration: '',
    exercise_description: '',
    hours_of_sleep: '',
  });

  const [csrfToken, setCsrfToken] = useState('');

  // Fetch CSRF token when the component mounts
  useEffect(() => {
    const token = document.querySelector('[name=csrfmiddlewaretoken]')?.value;
    if (token) {
      setCsrfToken(token);
    }
  }, []);

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if the CSRF token is available
    if (!csrfToken) {
      console.error('CSRF token not found');
      return;
    }

    // Submit the form data to the backend
    try {
      const response = await fetch('/api/journal/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-CSRFToken': csrfToken, // CSRF token in the header
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        console.log('Journal entry submitted successfully');
        resetForm(); // Reset form after successful submission
      } else {
        console.error('Error submitting journal entry:', response.statusText);
      }
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  // Reset the form
  const resetForm = () => {
    setFormData({
      date: '',
      content: '',
      mood_color: '',
      proper_nutrition: false,
      proper_hydration: false,
      hydration_amount: '',
      proper_exercise: false,
      exercise_duration: '',
      exercise_description: '',
      hours_of_sleep: '',
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* CSRF Token */}
      <input
        type="hidden"
        name="csrfmiddlewaretoken"
        value={csrfToken}
      />
      
      {/* Date Input */}
      <div className="form-group">
        <label>Date</label>
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          className="form-control"
        />
      </div>

      {/* Content Input */}
      <div className="form-group">
        <label>Content</label>
        <textarea
          name="content"
          value={formData.content}
          onChange={handleChange}
          className="form-control"
        />
      </div>

      {/* Mood Color Select */}
      <div className="form-group">
        <label>Mood color</label>
        <select
          name="mood_color"
          value={formData.mood_color}
          onChange={handleChange}
          className="form-control"
        >
          <option value="#FFFF00">Happy</option>
          <option value="#0000FF">Sad</option>
          <option value="#FF0000">Angry</option>
          <option value="#00FF00">Relaxed</option>
          <option value="#808080">Neutral</option>
        </select>
      </div>

      {/* Proper Nutrition Checkbox */}
      <div className="form-group">
        <label>Proper nutrition</label>
        <input
          type="checkbox"
          name="proper_nutrition"
          checked={formData.proper_nutrition}
          onChange={handleChange}
        />
      </div>

      {/* Proper Hydration Checkbox */}
      <div className="form-group">
        <label>Proper hydration</label>
        <input
          type="checkbox"
          name="proper_hydration"
          checked={formData.proper_hydration}
          onChange={handleChange}
        />
      </div>

      {/* Hydration Amount Input */}
      <div className="form-group">
        <label>Hydration amount (L)</label>
        <input
          type="number"
          name="hydration_amount"
          value={formData.hydration_amount}
          onChange={handleChange}
          className="form-control"
        />
      </div>

      {/* Proper Exercise Checkbox */}
      <div className="form-group">
        <label>Proper exercise</label>
        <input
          type="checkbox"
          name="proper_exercise"
          checked={formData.proper_exercise}
          onChange={handleChange}
        />
      </div>

      {/* Exercise Duration Input */}
      <div className="form-group">
        <label>Exercise duration (mins)</label>
        <input
          type="number"
          name="exercise_duration"
          value={formData.exercise_duration}
          onChange={handleChange}
          className="form-control"
        />
      </div>

      {/* Exercise Description Input */}
      <div className="form-group">
        <label>Exercise description</label>
        <textarea
          name="exercise_description"
          value={formData.exercise_description}
          onChange={handleChange}
          className="form-control"
        />
      </div>

      {/* Hours of Sleep Input */}
      <div className="form-group">
        <label>Hours of sleep</label>
        <input
          type="number"
          name="hours_of_sleep"
          value={formData.hours_of_sleep}
          onChange={handleChange}
          className="form-control"
        />
      </div>

      {/* Submit Button */}
      <div className="form-actions">
        <button type="submit" className="btn btn-primary">Submit</button>
      </div>
    </form>
  );
};

export default JournalForm;
