import * as React from 'react';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { Controller } from 'react-hook-form';

export default function SelectVariants(props) {
  const { label, name, control, moodColors } = props; // Pass moodColors as a prop

  return (
    <div>
      <FormControl variant="standard" sx={{ m: 1, minWidth: 120 }}>
        <InputLabel id="demo-simple-select-standard-label">{label}</InputLabel>
        <Controller
          name={name}
          control={control}
          render={({ field: { onChange, value }, fieldState: { error }, formState }) => (
            <Select
              labelId="demo-simple-select-standard-label"
              id="demo-simple-select-standard"
              label={label}
              value={value} // Pass the controlled value
              onChange={onChange} // Bind the onChange handler
            >
              {moodColors.map((color) => (
                <MenuItem key={color.value} value={color.value}>
                  {color.label}
                </MenuItem>
              ))}
            </Select>
          )}
        />
      </FormControl>
    </div>
  );
}
